jQuery(document).ready(function(o) {
    o(".perfil").click(function(o) {
        var t = o.target.id;
        window.location.href = window.location.href + "?user_id=" + t;
    }), o(".perfil_volver").click(function() {
        window.location.href = window.location.href.split("?user_id")[0];
    }), o(".boletas_button").click(function() {
        o(".facturas").hide(), o(".boletas").show(), o("h1").text("Boletas");
    }), o(".facturas_button").click(function() {
        o(".boletas").hide(), o(".facturas").show(), o("h1").text("Facturas");
    }), o(".todos_button").click(function() {
        o(".facturas").show(), o(".boletas").show(), o("h1").text("Pagos");
    }), o(".ver_pagos").click(function() {
        window.location.href = window.location.href + "pagos";
    }), o(".ver_perfiles").click(function() {
        window.location.href = window.location.href + "perfiles";
    });
});