jQuery(document).ready(function(t) {
    t(".perfil").click(function(t) {
        var n = t.target.id;
        window.location.href = window.location.href + "?user_id=" + n;
    }), t(".perfil_volver").click(function() {
        window.location.href = window.location.href.split("?user_id")[0];
    }), t(".boletas_button").click(function() {
        t(".factura").hide(), t(".boleta").show(), t("h1").text("Boletas");
    }), t(".facturas_button").click(function() {
        t(".boleta").hide(), t(".factura").show(), t("h1").text("Facturas");
    }), t(".todos_button").click(function() {
        t(".factura").show(), t(".boleta").show(), t("h1").text("Pagos");
    }), t(".ver_pagos").click(function() {
        window.location.href = window.location.href + "pagos";
    }), t(".ver_perfiles").click(function() {
        window.location.href = window.location.href + "perfiles";
    }), t(".ver_campanas").click(function() {
        window.location.href = window.location.href + "campana/listado";
    }), t(".ver_comentarios").click(function() {
        window.location.href = window.location.href + "comentarios";
    }), t(".agregar_campana").click(function() {
        window.location.href = window.location.href + "campanas/nueva";
    }), t(".ver_todas").click(function() {
        var n = t(".btn-success").text().slice(0, -1).toLowerCase();
        console.log(n), n ? (t("." + n + ".pagada_content").show(), t("." + n + ".pendiente_content").show()) : (t(".pagada_content").show(), 
        t(".pendiente_content").show());
    }), t(".ver_pagadas").click(function() {
        var n = t(".btn-success").text().slice(0, -1).toLowerCase();
        n ? (t("." + n + ".pendiente_content").hide(), t("." + n + ".pagada_content").show()) : (t(".pendiente_content").hide(), 
        t(".pagada_content").show());
    }), t(".ver_pendientes").click(function() {
        var n = t(".btn-success").text().slice(0, -1).toLowerCase();
        n ? (t("." + n + ".pagada_content").hide(), t("." + n + ".pendiente_content").show()) : (t(".pagada_content").hide(), 
        t(".pendiente_content").show());
    }), t(".btn-sm").click(function() {
        t(this).removeClass("btn-default"), t(this).siblings().removeClass("btn-danger"), 
        t(this).siblings().addClass("btn-default"), t(this).addClass("btn-danger");
    }), t(".btn-md").click(function() {
        t(this).removeClass("btn-default"), t(this).siblings().removeClass("btn-success btn-danger"), 
        t(this).siblings().addClass("btn-default"), t(this).addClass("btn-success");
    }), t(".filtro_valor").click(function() {
        t(this).parent().siblings().removeClass("active"), t(this).parent().addClass("active"), 
        t(".columna_oc").hide(), t(".columna_status").hide(), t(".columna_boleta").hide(), 
        t(".columna_valor").show();
    }), t(".filtro_oc").click(function() {
        t(this).parent().siblings().removeClass("active"), t(this).parent().addClass("active"), 
        t(".columna_valor").hide(), t(".columna_status").hide(), t(".columna_boleta").hide(), 
        t(".columna_oc").show();
    }), t(".filtro_status").click(function() {
        t(this).parent().siblings().removeClass("active"), t(this).parent().addClass("active"), 
        t(".columna_valor").hide(), t(".columna_oc").hide(), t(".columna_boleta").hide(), 
        t(".columna_status").show();
    }), t(".filtro_boleta").click(function() {
        t(this).parent().siblings().removeClass("active"), t(this).parent().addClass("active"), 
        t(".columna_valor").hide(), t(".columna_oc").hide(), t(".columna_status").hide(), 
        t(".columna_boleta").show();
    });
});